import React, { useState } from "react";
import { Link, Navigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import axios from "axios";

export default function Signup() {
    const [credentials, setCredentails] = useState({name:"", email:"", password: "", location:""})
    const handleSubmit = async(e) => {
        e.preventDefault();
        try{
          const resposne = await axios.post("http://localhost:5000/api/createuser", {...credentials})
          // console.log(resposne)
          if(resposne && resposne.status === 200){
            alert(resposne.data.message)
            Navigate("/login")
          }else{
            alert(res.data.message)
          }
        }catch(err){
          // console.log(err.resposne)
          alert("Something went wrong")
        }
    }
    const eventListener = (e) => {
      setCredentails({...credentials, [e.target.name]: e.target.value })
    }
  return (
    <>
    <div>
      <Navbar/>
    </div>
      <div className="container mt-5 mb-5">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              placeholder="Enter name"
              name="name"
              value={credentials.name}
              onChange={eventListener}
            />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              placeholder="Enter email"
              name="email"
              value={credentials.email}
              onChange={eventListener}
            />
            <small id="emailHelp" className="form-text text-muted">
              We'll never share your email with anyone else.
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputPassword1">Password</label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword1"
              placeholder="Password"
              name="password"
              value={credentials.password}
              onChange={eventListener}
            />
          </div>
          <div className="form-group">
            <label htmlFor="location">Address</label>
            <input
              type="text"
              className="form-control"
              id="location"
              placeholder="Enter address"
              name="location"
              value={credentials.location}
              onChange={eventListener}
            />
          </div>
          <button type="submit" className="btn btn-success">
            Submit
          </button>
          <Link to='/login' className="m-3 btn btn-danger">Already a user</Link>
        </form>
      </div>
      <div>
      <Footer/>
    </div>
    </>
  );
}
